<template>    <div class="product-list main-container" :key="num">        <div class="product-list-header">            <h1 v-if="data">{{data.Data['搜索标题']}}</h1>            <span v-if="data">{{data.Data['商品总数']}}个产品</span>            <div>                显示筛选器                <i class="thumb-up"></i>            </div>        </div>        <div class="product-list-tip">            <div>                <span>按系列查看</span>                <span>按颜色查看</span>            </div>        </div>        <ul class="product-list-item" v-if="data">            <template v-for="(item,key) in data.DSet.Table1">                <router-link                    tag="li"                    :to="'/detail/'+item['商品编号']"                    class="item"                    :key="key">                    <div class="product-list-img">                        <img :src="item['缩略图']" alt=" " class="">                    </div>                    <p class="product-tip">{{item['商品名称']}}</p>                    <p class="price">￥{{item['折扣价']}}</p>                </router-link>                <li class="line" v-if="(key+1)%3==0"></li>            </template>        </ul>    </div></template><script>	export default {		data: function () {			return {				data: null,				num: 0,//ajax后刷新页面			}		},		computed: {			cid: function () {				return this.$route.params.cid;			}		},		watch: {			$route: function (to, from) {				this.getData();			}		},		methods: {//			获取数据			getData: function () {				var _this = this;				$.ajax({					type: 'post',					url: 'http://116.55.233.249/Ajax/X.ashx',					dataType: 'json',					data: {						c: 1001,						cid: _this.$route.params.cid,					},					success: function (data) {						if (data.Success == true) {							_this.num++;							_this.data = data;						}					},					error: function (data) {						console.log('获取产品信息失败：' + data)					}				})			}		},		created: function () {			this.getData();		}	}</script><style scoped>    .product-list {        overflow: hidden;    }    .product-list-header {        width: 1158px;        height: 48px;        margin: 0 auto;        line-height: 48px;        overflow: hidden;        position: relative;    }    .product-list-header h1 {        float: left;        font-size: 26px;        letter-spacing: 4px;        margin-right: 20px;        color: #666;    }    .product-list-header span {        float: left;    }    .product-list-header div {        width: 130px;        float: right;        font-size: 14px;        border-left: 1px solid #d8d8d8;        background: #ebeae5;        text-align: left;        text-indent: 20px;        position: relative;        cursor: pointer;    }    .thumb-up {        position: absolute;        right: 10px;        top: 22px;        border-width: 4px;        border-style: solid;        border-color: black transparent transparent transparent;    }    .product-list-tip {        height: 48px;        line-height: 48px;        border-top: 1px solid #d8d8d8;        border-bottom: 1px solid #d8d8d8;        background: #ebeae5;    }    .product-list-tip > div {        width: 1158px;        margin: 0 auto;    }    .product-list-tip span {        display: inline-block;        width: 50%;        text-align: left;        color: #333;        font-size: 14px;        cursor: pointer;    }    .product-list-item {        width: 1158px;        margin: 30px auto 40px;        overflow: hidden;        border-top: 1px solid #d1d1d1;    }    .product-list-item li.item {        float: left;        width: 386px;        height: 466px;        box-sizing: border-box;        border: 10px solid #fff;        cursor: pointer;    }    .product-list-img {        height: 300px;        margin: 40px 10px 60px;        position: relative;    }    .product-list-img > img {        width: 300px;        height: 300px;    }    .product-list-item li.item:hover {        border-color: #b8b8b8;    }    .product-list-item li.line {        float: left;        clear: both;        width: 100%;        height: 1px;        background: #d1d1d1;    }    .product-tip {        font-size: 16px;        font-weight: 500;    }    .price {        font-size: 14px;    }</style>