<template>    <div class="tea main-container">        <ul class="wrap">            <li class="bg-5" v-for="item in ary"  :style="{backgroundImage:'url(' + item['背景图'] + ')',width:100/ary.length+'%'}">                <a href="javascript:;">{{item['类别名称'].slice(0,2)}}<br>{{item['类别名称'].slice(2)}}</a>            </li>        </ul>        <div class="tea-img" :style="{backgroundImage:'url(' + imgUrl + ')'}"></div>    </div></template><script>	export default {		data: function () {			return {				ary:'',                imgUrl:'',//背景图            }		},		created: function () {			var _this=this;			$.ajax({				type: 'post',				url: 'http://116.55.233.249/Ajax/X.ashx',				dataType: 'json',				data: {					c: 1000				},				success: function (data) {					if (data.Success == true) {						var submenu = data.DSet.Table2,							ary = [],							obj = {},							obj2 = {								'10': [],								'20': [],								'30': [],								'40': [],								'50': [],							};						for (var i in submenu) {							if (submenu[i]['类别编号'].length == 4) {								obj[submenu[i]['类别编号']] = [];							}							ary.push(submenu[i]);						}						for (var i in obj) {							for (var j in ary) {								if (ary[j]['类别编号'].slice(0, 4) == i) {									obj[i].push(ary[j])								}							}						}						for (var i in obj2) {							for (j in obj) {								if (obj[j][0]['大类编号'] == i) {									obj2[i].push(obj[j])								}							}						}						var ary3=obj2[30],//普洱茶页面信息							ary4=[],//普洱茶页面上面图片信息							headers=data.DSet.Table1;//头部信息						for(var j in headers){							if(headers[j]['大类编号']==30){								_this.imgUrl=headers[j]['背景图'];							}						}						for(var i in ary3){							ary4.push(ary3[i][0])						}						_this.$set(_this.$data,'ary',ary4)					}				},				error: function (data) {					console.log('获取数据失败：' + data);				}			});		},	}</script><style scoped>    .tea {        height: 1110px;        overflow: hidden;        background: #000;        position: relative;        z-index: 1;    }    .wrap {        width: 1000px;        height: 660px;        background: transparent;        margin: 24px auto;        position: relative;        z-index: 2;    }    .wrap li {        float: left;        background-size: 100% 660px;        box-sizing: border-box;        overflow: hidden;    }    .wrap li a {        width: 100%;        height: 660px;        font-size: 36px;        display: flex;        flex: 1;        align-items: center;        justify-content: center;        color: #ccc;        background: rgba(0, 0, 0, 0.7);        opacity: 0;        transition: all .5s;        box-sizing: border-box;    }    .wrap li:hover a {        opacity: 1;    }    .tea-img {        margin-top: -80px;        z-index: 1;        position: relative;        width: 100%;        height: 510px;        background-position: center;        background-repeat: no-repeat;        background-image: url(/img/tea_bg.jpg);    }</style>